mapper.insert:
INSERT INTO BOARD(
			BNO
		, MENU_ID
		, TITLE
		, CONTENT
		, WRITER
		, REGDATE
		, HIT
)
 VALUES (
 			(SELECT NVL(MAX(BNO), 0)+1 FROM BOARD)
 		, #{menu_id}
 		, #{title}
 		, #{content}
 		, #{writer}
 		, SYSDATE
 		, 0
 )

insert.foreach:
  	INTO FILES VALUES (	  	
	  		GET_FILENUM()                
	  		, (SELECT MAX(BNO) FROM BOARD)
	  		, #{file.filename}
	  		, #{file.fileext}
	  		, #{file.sfilename}	
  	)

mapper.update:
UPDATE  BOARD
 SET    HIT = HIT + 1
 WHERE  BNO = #{bno}

UPDATE 	  BOARD
 SET			TITLE   = #{title}
 				, CONTENT = #{content}
 WHERE  	BNO = #{bno}
